            "deps": ["jquery", "css!../plugins/bootstrap-3.3.7/css/bootstrap.min.css", "css!../plugins/Font-Awesome-3.2.1/css/font-awesome.min.css"]
        }
    }
});

//入口配置加载完后马上引用bootstarp模块
//反正每个页面都要使用jquery、bootstarp、font - awesome

