    input[type="text"]:enabled {
        /*启用的文本框*/
        background-color: #f8dede;
    }
    input[type="text"]:disabled {
        /*禁用的文本框*/
        background-color: aqua;
    }
    input[type="text"]:focus {
        /*获得焦点的文本框*/
        background-color: red;
    }
